import React, { useRef, useState } from "react";
import "../styles/Tourdetails.css";
import { Container, Row, Col, Form, ListGroup } from "reactstrap";
import { useParams } from "react-router-dom";
import tourData from "../assets/data/tours";
import calculateAvgRating from "../utils/calculateAvgRating";
import avatar from "../assets/images/avatar.jpg";
import Booking from "../components/Booking/Booking";

const Tourdetails = () => {
  const { id } = useParams();
  const reviewMsgref = useRef("");
  const [tourRating, settourRating] = useState(null);
  const tour = tourData.find((tour) => tour.id === id);

  if (!tour) {
    return <p>Tour not found.</p>; // Handle case where tour is not found
  }

  const {
    photo,
    title,
    desc,
    price,
    address,
    reviews,
    city,
    distance,
    maxGroupSize,
  } = tour;

  const { totalRating, avgRating } = calculateAvgRating(reviews);

  const options = { day: "numeric", month: "long", year: "numeric" };

  const submitHandler = (e) => {
    e.preventDefault();
    const reviewText = reviewMsgref.current.value;
  };

  return (
    <section>
      <Container>
        <Row>
          <Col lg="8">
            <div className="tour__content">
              <img src={photo} alt={title} />
              <div className="tour__info">
                <h2>{title}</h2>
                <div className="d-flex align-items-center gap-5">
                  <span className="tour__location d-flex align-items-center gap-1">
                    <i
                      className="ri-star-line"
                      style={{ color: "var(--secondary-color)" }}
                    ></i>{" "}
                    {avgRating === 0 ? null : avgRating}
                    {totalRating === 0 ? (
                      "Not Rated"
                    ) : (
                      <span>({reviews?.length})</span>
                    )}
                  </span>
                  <span>
                    <i className="ri-map-pin-fill"></i> {address}
                  </span>
                </div>
                <div className="tour__extra-details">
                  <span>
                    <i className="ri-map-pin-2-line"></i> {city}{" "}
                  </span>
                  <span>
                    <i className="ri-money-dollar-circle-line"></i> ${price}{" "}
                    /per person
                  </span>
                  <span>
                    <i className="ri-map-pin-2-line"></i> {distance} k/m
                  </span>
                  <span>
                    <i className="ri-group-line"></i> {maxGroupSize} Persons
                  </span>
                </div>
                <h5>Description</h5>
                <p>{desc}</p>{" "}
                {/* Keep this or the one above, depending on your design */}
              </div>
              <div className="tour__reviews mt-4">
                <h4>Reviews ({reviews?.length} reviews)</h4>
                <Form onSubmit={submitHandler}>
                  <div className="d-flex align-items-center gap-3 mb-4 rating__group">
                    <span onClick={() => settourRating(1)}>
                      1<i class="ri-star-s-fill"></i>
                    </span>
                    <span onClick={() => settourRating(2)}>
                      2<i class="ri-star-s-fill"></i>
                    </span>
                    <span onClick={() => settourRating(3)}>
                      3<i class="ri-star-s-fill"></i>
                    </span>
                    <span onClick={() => settourRating(4)}>
                      4<i class="ri-star-s-fill"></i>
                    </span>
                    <span onClick={() => settourRating(5)}>
                      5<i class="ri-star-s-fill"></i>
                    </span>
                  </div>
                  <div className="review__input">
                    <input
                      type="text"
                      ref={reviewMsgref}
                      placeholder="share your thoughts"
                      required
                    />
                    <button
                      className="btn primary__btn text-white"
                      type="submit"
                    >
                      Submit
                    </button>
                  </div>
                </Form>
                <ListGroup className="user__reviews">
                  {reviews?.map((review) => (
                    <div className="review__item">
                      <img src={avatar} alt="avatar" />
                      <div className="w-100">
                        <div className="d-flex align-items-center justify-content-between">
                          <div>
                            <h5>Vishal</h5>
                            <p>
                              {new Date("2024-08-19").toLocaleDateString(
                                "en-US",
                                options
                              )}
                            </p>
                          </div>
                          <span className="d-flex align=items-center">
                            5<i class="ri-star-s-fill"></i>
                          </span>
                        </div>
                        <h6>Amazing tour</h6>
                      </div>
                    </div>
                  ))}
                </ListGroup>
              </div>
            </div>
          </Col>
          <Col lg='4'>
          <Booking />
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default Tourdetails;
